function pad(b) {
  const h = b.toString(16);
  return (h + "").length < 2 ? "0" + h : h;
}

function parse_wifi_legacy(devEui, bytes) {
    const mgs_query = {
        "deveui":devEui.match( /.{1,2}/g ).join( '-' ),
        "uplink":{
            "msgtype":"wifi",
            "payload": `01${bytes}`,
        }
    };

    bytes = Buffer.from(bytes, "hex");
    var addresses = [];
    for (var i = 0; i < bytes.length; i += 7) {
        var rssi_raw = bytes[i + 0];
        var bssid_slice = bytes.slice(i + 1, i + 7);
        var bssid = [];
        bssid_slice.forEach(byte => {
            bssid.push(pad(byte));
        });
        addresses.push({
            "macAddress": bssid.join(":"),
            "signalStrength":  (rssi_raw > 127) ? rssi_raw - 256 : rssi_raw,
        });
    }

    const lorawan = [{
        "gatewayId": "fake",
        "antennaId": 0,
        "rssi": 0,
        "snr":0,
        "antennaLocation": {
            "latitude": 0.0,
            "longitude": 0.0,
            "altitude": 0.0
        }
    }];

    var gls_query = {
       "lorawan": lorawan,
       "wifiAccessPoints": addresses,
    };
    gls_query.wifiAccessPoints.forEach(ap => {
        delete ap.type;
        delete ap.channel;
    });


    return {
        "type": "Wi-Fi_Legacy",
        "addresses": addresses,
        "raw": bytes,
        "gls_query": gls_query,
        "mgs_query": mgs_query,
    };
}

function parse_wifi(devEui, bytes) {
    const mgs_query = {
        "deveui":devEui.match( /.{1,2}/g ).join( '-' ),
        "uplink":{
            "msgtype":"wifi",
            "payload": `01${bytes.slice(10)}`,
        }
    };

    const version = parseInt(bytes.slice(0, 2), 16);
    const timestamp = parseInt(bytes.slice(2, 10), 16);

    bytes = Buffer.from(bytes + 10, "hex");
    var addresses = [];
    for (var i = 5; i < bytes.length; i += 7) {
        var rssi_raw = bytes[i + 0];
        var bssid_slice = bytes.slice(i + 1, i + 7);
        var bssid = [];
        bssid_slice.forEach(byte => {
            bssid.push(pad(byte));
        });
        addresses.push({
            "macAddress": bssid.join(":"),
            "signalStrength":  (rssi_raw > 127) ? rssi_raw - 256 : rssi_raw,
        });
    }

    const lorawan = [{
        "gatewayId": "fake",
        "antennaId": 0,
        "rssi": 0,
        "snr":0,
        "antennaLocation": {
            "latitude": 0.0,
            "longitude": 0.0,
            "altitude": 0.0
        }
    }];

    var gls_query = {
       "lorawan": lorawan,
       "wifiAccessPoints": addresses,
    };
    gls_query.wifiAccessPoints.forEach(ap => {
        delete ap.type;
        delete ap.channel;
    });

    return {
        "type": "Wi-Fi",
        "addresses": addresses,
        "timestamp": timestamp,
        "version": version,
        "raw": bytes,
        "gls_query": gls_query,
        "mgs_query": mgs_query,
    };
}

function parse_gnss(antenna, devEui, data) {
    const bit_mask_has_location = 1;
    const has_assisted_location = (parseInt(data.slice(6, 8), 16) & bit_mask_has_location) !== 0;
    var mgs_query = {
        "deveui":devEui.match( /.{1,2}/g ).join( "-" ),
        "uplink":{
            "msgtype":"gnss",
            "payload": data,
        }
    };
    if (has_assisted_location === false){
       mgs_query.uplink.gnss_assist_position = global.get("device_assistance_coordinates")
    }

    return {
        "type": `GNSS - ${antenna} antenna`,
        "data": data,
        "mgs_query": mgs_query,
    };
}

function parse_signed_2_bytes_int(value) {
    var value_int =  parseInt(value, 16);
    if ((value_int & 0x8000) > 0) {
        value_int = value_int - 0x10000;
    }
    return value_int;
}

function parse_acc(data) {
    const move_history = parseInt(data.slice(0, 2), 16);
    const acc = {
        'x': parse_signed_2_bytes_int(data.slice(2, 6), 16),
        'y': parse_signed_2_bytes_int(data.slice(6, 10), 16),
        'z': parse_signed_2_bytes_int(data.slice(10, 14), 16),
    };
    const temperature =  parse_signed_2_bytes_int(data.slice(14, 18), 16)/100;

    return {
        "type": "acc",
        "move_history": move_history,
        "acc_mg": acc,
        "temperature_C": temperature
    };
}

function parse_charge(data) {
    return {
        "type": "charge",
        "charge_mAh":  parseInt(data, 16),
    };
}

function parse_voltage(data) {
    return {
        "type": "voltage",
        "voltage_V":  parseInt(data, 16)/1000,
    };
}

function parse_counter(data) {

    const host_reset = parseInt(data.slice(0, 4), 16);
    const modem_reset = parseInt(data.slice(4, 8), 16);

    return {
        "type": "counter",
        "host_reset": host_reset,
        "modem_reset": modem_reset,
    };
}

function parse_sensors_full(data) {
    const version = parseInt(data.slice(0, 1), 16);
    const move_history = parseInt(data.slice(1, 2), 16);
    const temperature =  parse_signed_2_bytes_int(data.slice(2, 6), 16)/100;
    const acc_charge =  parse_signed_2_bytes_int(data.slice(6, 10), 16);
    const voltage =  parse_signed_2_bytes_int(data.slice(10, 14), 16);

    return {
        "type": "sensor_full",
		"version": version,
        "move_history": move_history,
        "temperature_C": temperature,
        "accumulated_charge": acc_charge,
        "voltage": voltage
    };
}

function parse_sensors_basic(data) {
    const version = parseInt(data.slice(0, 1), 16);
    const move_history = parseInt(data.slice(1, 2), 16);

    return {
        "type": "sensor_basic",
        "version": version,
        "move_history": move_history,
    };
}

function parse_gnss_multiframe(antenna, devEui, data) {
    const mgs_query = {
        "deveui":devEui.match( /.{1,2}/g ).join( "-" ),
        "uplink":{
            "msgtype":"gnss",
            "payload": data.slice(4,data.length),
        }
    };

    bytes = Buffer.from(data.slice(0,5), "hex");

    const token = bytes[0];
    const gnss_scan_idx = bytes[1]>>3;
    const nb_satellites = bytes[1]-(gnss_scan_idx<<3);
    return {
        "type": `GNSS ${antenna}`,
        "data": data.slice(4,data.length),
        "mgs_query": mgs_query,
        "nb_of_satellite":nb_satellites,
        "token":token,
        "gnss_scan_idx": gnss_scan_idx,
    };
}

