var grouping_fifo = fifos.grouping || {'token': actual_token, 'fifo': []};
const stored_token = grouping_fifo.token;

// Cases to handle:
// 1. Token still the same, not last NAV
//    -> Just store new NAV
// 2. Token has changed, not the last NAV
//    -> Send the previous group, start new one with NAV just received
// 3. Token the same, last NAV
//    -> Store the just received NAV, send the current group
// 4. Token has changed, last NAV
//    -> Send the previous group
//    -> Create a new group with NAV just received and send it (will
//       generate a single-multi-capture group which may be considered
//       an error by loracloud)

const is_token_the_same = stored_token == actual_token;
const is_last = msg.data.is_last;

if(is_token_the_same === true){
    // Token is still the same: the nav message is just pushed to storage
    grouping_fifo.fifo.push(nav_message);
}
else{
    // The token just changed: the request for solve is prepared, and the new nav and token
    // are stored to prepare the next group
    msg.payload = grouping_fifo.fifo;
    msg.data.type = "GNSS grouping multi-frame";
    grouping_fifo.token = actual_token;
    grouping_fifo.fifo = [nav_message];
    node.send(msg);
}

if(is_last === true){
    msg.payload = grouping_fifo.fifo;
    msg.data.type = "GNSS grouping multi-frame";
    delete fifos.grouping;
    fifos_per_deveui[deveui] = fifos;
    node.send(msg);
}
else{
    fifos.grouping = grouping_fifo;
    fifos_per_deveui[deveui] = fifos;
}

